import * as cell from './cell';
import * as col from './col';
import * as file from './file';
import * as lib from './lib';
import * as row from './row';
import * as sheet from './sheet';
import * as style from './style';
import Zip from 'jszip';

export default { ...cell, ...col, ...file, ...lib, ...row, ...sheet, ...style, Zip };

function attrEscape (str) {
  return str.replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/"/g, '&quot;')
    .replace(/\t/g, '&#x9;')
    .replace(/\n/g, '&#xA;')
    .replace(/\r/g, '&#xD;');
}
function escape (str) {
  return str.replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/\r/g, '&#xD;');
}

export const HEAD = Symbol('head');

export function props (...keys) {
  return (target) => {
    for (const key of keys) {
      target.elements.push({
        key,
        kind: 'method',
        placement: 'prototype',
        descriptor: {
          get () {
            if (this.attributes) {
              return this.attributes[key];
            }
          },
          set (value) {
            if (this.attributes === undefined) {
              this.attributes = {};
            }
            this.attributes[key] = value;
          },
          configurable: true,
          enumerable: true
        }
      });
    }
    return target;
  };
}

export class Node {
  constructor (attributes = {}, children = [], name) {
    for (const key of Object.keys(attributes)) {
      this[key] = attributes[key];
    }
    this.children = children;
    this.__name = name || this.constructor.name.substring(1);
  }
  render () {
    function walk (tree) {
      const name = tree.__name;
      const { attributes, children } = tree;
      const tokens = [];

      if (tree[HEAD]) {
        tokens.push(tree[HEAD]);
      }
      tokens.push(`<${name}`);

      for (const key of Object.keys(attributes || {})) {
        let v = attributes[key];
        if (v === undefined) continue;
        if (typeof v === 'string') {
          v = attrEscape(v);
        }
        if (typeof v === 'boolean') {
          v = v ? 1 : 0;
        }
        tokens.push(` ${key}="${v}"`);
      }

      if (!children.length) {
        tokens.push('/>');
        return tokens;
      }
      tokens.push('>');
      for (const child of children) {
        if (child instanceof Node) {
          tokens.push(child.render());
        } else if (typeof child === 'string') {
          tokens.push(escape(child));
        } else {
          tokens.push(child.toString());
        }
      }
      tokens.push(`</${name}>`);
      return tokens;
    }
    return walk(this).join('');
  }
}

import { Xsst, Xsi, Xt } from './xmlSharedStrings';

export class RefTable {
  constructor () {
    this.strings = [];
    this.known = {};
  }
  makeXsst () {
    const len = this.strings.length;
    const sst = new Xsst({
      count: len,
      uniqueCount: len
    });
    for (const str of this.strings) {
      const si = new Xsi({}, [new Xt({}, [str])]);
      sst.children.push(si);
    }
    return sst;
  }
  addString (str) {
    if (this.known[str] === undefined) {
      const index = this.strings.length;
      this.strings.push(str);
      this.known[str] = index;
      return index;
    }
    return this.known[str];
  }
  getString (index) {
    return this.strings[index];
  }
  length () {
    return this.strings.length;
  }
}

import { Cell } from './cell';

/**
 * Row of the sheet.
 * ```js
 * const row = sheet.addRow();
 * row.setHeightCM(0.8);
 * ```
 */
export class Row {
  cells = [];
  hidden = false;
  /**
   * Row height
   * @type {Number}
   */
  height = 0;
  outlineLevel = 0;
  isCustom = false;

  constructor ({ sheet }) {
    this.sheet = sheet;
  }
  /**
   * Set height of the Row with `cm` unit.
   * @param {Number} ht Height with `cm` unit
   */
  setHeightCM (ht) {
    this.height = ht * 28.3464567;
    this.isCustom = true;
  }
  /**
   * Create a cell and add it into the Row.
   * @return {Cell}
   */
  addCell () {
    const cell = new Cell({ row: this });
    this.cells.push(cell);
    this.sheet.maybeAddCol(this.cells.length);
    return cell;
  }
}

